/**
 * Intelligently autoclear form inputs
 *
 * See README for usage
 *
 * Based on Matt Lunn's http://www.mattlunn.me.uk/projects/autoclear
 *
 * @author   Joshua Priddle <jpriddle@nevercraft.net>
 * @version  0.1
 * @param    Object
 * @see      http://blog.nevercraft.net/projects/jquery-autoclear-live
 * @see      README
 */
(function($){$.fn.autoclear=function(c){var d={default_class:'default',default_val:''};$.extend(d,c);function empty(a){if(a.attr('value')==a.attr('title')){a.removeClass(d.default_class).attr('value','')}}function fill(a){var b=a.attr('title'),current_val=a.attr('value');if(current_val===""||current_val===b){a.addClass(d.default_class).attr('value',b)}}function is_set(a){return a!==undefined&&$.trim(a)!==""}this.live('focus.autoclear',function(){empty($(this))}).live('blur.autoclear init.autoclear',function(){fill($(this))});this.each(function(){var a=$(this),current_val=a.attr('value'),default_val=a.attr('title');if(!is_set(current_val)&&is_set(default_val)){a.trigger('init.autoclear')}else if(!is_set(default_val)){a.attr('title',d.default_val);a.trigger('init.autoclear')}})}})(jQuery);
